Component({
  data: {
    open: false // card在上还是下

  },
  props: {
    show: true,
    // 是否显示card
    data: {},
    onNavigation: function onNavigation() {},
    onCloseCard: function onCloseCard() {}
  },
  methods: {
    /**
     * 点击card上方箭头
     * @method onMove
     */
    onMove: function onMove() {
      this.setData({
        open: !this.data.open
      });
    },

    /**
     * 点击右上角关闭
     * @method onClose
     */
    onClose: function onClose(e) {
      this.props.onCloseCard(e);
    },

    /**
     * 点击立即前往
     * @method onNavigation
     */
    onNavigation: function onNavigation(e) {
      this.props.onNavigation(e);
    }
  }
});