import parse from 'mini-html-parser2';
Component({
  data: {
    nodes: []
  },
  props: {
    html: ''
  },
  didMount: function didMount() {
    var _this = this;

    var html = this.props.html;
    parse(html, function (err, nodes) {
      if (!err) {
        _this.setData({
          nodes: nodes
        });
      }
    });
  }
});