Component({
  data: {
    items: []
  },
  props: {
    className: '',
    direction: 'vertical',
    chooseData: {}
  },
  didMount: function didMount() {
    var chooseData = this.props.chooseData;
    this.setData({
      items: chooseData
    });
  },
  didUpdate: function didUpdate() {
    this.setData({
      items: this.props.chooseData
    });
  },
  methods: {
    choose: function choose(e) {
      this.props.onChoose(e.target.dataset.chooseData);
    },
    chooseName: function chooseName(e) {
      // 点击景点
      this.props.onTapname(e.target.dataset.chooseData);
    }
  }
});