var DEFAULT_ROW_WIDTH = '100%';
var DEFAULT_LAST_ROW_WIDTH = '60%';
Component({
  props: {
    loading: true,
    showAvatar: true,
    avatarSize: '50px',
    avatarShape: 'round',
    showTitle: true,
    titleWidth: '40%',
    row: 3,
    animate: true
  },
  data: {
    rowList: []
  },
  didMount: function didMount() {
    var list = [];
    var row = this.props.row;

    for (var i = 0; i < row; i++) {
      list.push({
        width: i === row - 1 && i !== 0 ? DEFAULT_LAST_ROW_WIDTH : DEFAULT_ROW_WIDTH
      });
    }

    this.setData({
      rowList: list
    });
  }
});