/**
 * 摇一摇
 * @param {func} success
 */
export function watchPhoneShake() {
  var success = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : function () {};

  my.watchShake({ success: success });
}

/**
 * 调用手机震动
 * @param {func} success
 */
export function callPhoneVibrate() {
  var success = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : function () {};

  my.vibrate({ success: success });
}

export function toast(msg) {
  var duration = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 3000;

  my.showToast({
    content: JSON.stringify(msg),
    duration: duration
  });
}